import React, { useState } from 'react';
import axios from 'axios';
import './BuyProperty.css';

const BuyProperty = () => {
  const [buyerDetails, setBuyerDetails] = useState({
    name: '',
    email: '',
    phone: '',
    pincode: '', // Added pincode field
    propertyType: 'buy', // Default to 'buy' type
  });

  const [properties, setProperties] = useState([]); // State to store properties
  const [loading, setLoading] = useState(false); // State to show loading spinner

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBuyerDetails({ ...buyerDetails, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading

    try {
      // Send the buyer details to the backend (you might need to update this API)
      await axios.post('http://localhost:5000/api/buy', buyerDetails);
      alert('Purchase Request Sent!');

      // Fetch properties based on the entered pincode
      const response = await axios.get(`http://localhost:5000/api/properties?pincode=${buyerDetails.pincode}`);
      setProperties(response.data); // Set the properties from response
    } catch (error) {
      alert('Error in Purchase or Fetching Properties');
      console.error(error);
    } finally {
      setLoading(false); // End loading
    }
  };

  return (
    <div className="buy-property-container">

      <h2>Buy or Rent Property</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={buyerDetails.name}
          onChange={handleInputChange}
          placeholder="Name"
          required
        />
        <input
          type="email"
          name="email"
          value={buyerDetails.email}
          onChange={handleInputChange}
          placeholder="Email"
          required
        />
        <input
          type="text"
          name="phone"
          value={buyerDetails.phone}
          onChange={handleInputChange}
          placeholder="Phone Number"
          required
        />
        <input
          type="text"
          name="pincode"
          value={buyerDetails.pincode}
          onChange={handleInputChange}
          placeholder="Pincode"
          required
        />
        <select
          name="propertyType"
          value={buyerDetails.propertyType}
          onChange={handleInputChange}
        >
          <option value="buy">Buy</option>
          <option value="rent">Rent</option>
        </select>
        <button type="submit">Submit</button>
      </form>

      {loading && <p>Loading properties...</p>}

      {/* Displaying properties */}
      {properties.length > 0 && (
        <div className="property-list">
          <h3>Available Properties</h3>
          <div className="property-cards">
            {properties.map((property) => (
              <div key={property._id} className="property-card">
                <h4>{property.name}</h4>
                <p>{property.description}</p>
                <p><strong>Price:</strong> {property.price}</p>
                <p><strong>Location:</strong> {property.location}</p>
                <p><strong>Contact:</strong> {property.contact}</p>
                <p><strong>Property Type:</strong> {property.propertyType}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default BuyProperty;
