import React, { useState } from 'react';
import axios from 'axios';

const BuyProperty = () => {
  const [buyerDetails, setBuyerDetails] = useState({
    name: '',
    email: '',
    phone: '',
    propertyType: 'buy', // Default to 'buy' type
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBuyerDetails({ ...buyerDetails, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/buy', buyerDetails);
      alert('Purchase Request Sent!');
    } catch (error) {
      alert('Error in Purchase');
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Buy or Rent Property</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={buyerDetails.name}
          onChange={handleInputChange}
          placeholder="Name"
          required
        />
        <input
          type="email"
          name="email"
          value={buyerDetails.email}
          onChange={handleInputChange}
          placeholder="Email"
          required
        />
        <input
          type="phone"
          name="phone"
          value={buyerDetails.phone}
          onChange={handleInputChange}
          placeholder="Phone Number"
          required
        />
        <select
          name="propertyType"
          value={buyerDetails.propertyType}
          onChange={handleInputChange}
        >
          <option value="buy">Buy</option>
          <option value="rent">Rent</option>
        </select>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default BuyProperty;
