import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddProperty.css'; // Create this CSS file

const AddProperty = () => {
  const [propertyDetails, setPropertyDetails] = useState({
    name: '',
    description: '',
    price: '',
    pincode: '',
    propertyType: '',
  });

  const [properties, setProperties] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPropertyDetails({ ...propertyDetails, [name]: value });
  };

  const fetchProperties = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/properties');
      setProperties(response.data);
    } catch (error) {
      console.error('Error fetching properties:', error);
    }
  };

  useEffect(() => {
    fetchProperties();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/properties', propertyDetails);
      alert('Property Added Successfully!');
      setPropertyDetails({ name: '', description: '', price: '', pincode: '', propertyType: '' });
      fetchProperties(); // refresh list
    } catch (error) {
      alert('Error Adding Property');
      console.error(error);
    }
  };

  return (
    <div className="add-property-container">
      <h2>Add New Property</h2>
      <form className="property-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={propertyDetails.name}
          onChange={handleInputChange}
          placeholder="Property Name"
          required
        />
        <textarea
          name="description"
          value={propertyDetails.description}
          onChange={handleInputChange}
          placeholder="Property Description"
          required
        />
        <input
          type="number"
          name="price"
          value={propertyDetails.price}
          onChange={handleInputChange}
          placeholder="Price"
          required
        />
        <input
          type="text"
          name="pincode"
          value={propertyDetails.pincode}
          onChange={handleInputChange}
          placeholder="Pincode"
          required
        />
        <input
          type="text"
          name="propertyType"
          value={propertyDetails.propertyType}
          onChange={handleInputChange}
          placeholder="Property Type"
          required
        />
        <button type="submit">Submit</button>
      </form>

      <h3 className="existing-title">Previously Added Properties</h3>
      <div className="property-list">
        {properties.map((property) => (
          <div key={property._id} className="property-card">
            <h4>{property.name}</h4>
            <p><strong>Type:</strong> {property.propertyType}</p>
            <p><strong>Description:</strong> {property.description}</p>
            <p><strong>Price:</strong> ₹{property.price}</p>
            <p><strong>Pincode:</strong> {property.pincode}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddProperty;
